    useEffect( () => {
        const timer = setTimeout( () => {
            setCopied(false)
        },1000)
    },[copied])
  return (
    <div className='result'>
        <p>hello</p>
        
        <CopyToClipboard 
        text={shortenLink}
        onCopy ={() => setCopied(true)}
