// Component input Bar 

import React, { useState } from 'react';
import axios from "axios";
import './InputContainer.css'
// import './Components_styling/InputContainer.css'

function InputBar(){

    const [input,setInput] = useState("");
    const [url,seturl] = useState("");
    const [urlList,setList] = useState([]);
    const [loading,setLoading] = useState(false);

    const baseUrl = `https://api.shrtco.de/v2/shorten?url=${input}`

    const Input = (e) =>{
        setInput(e.target.value);
        // console.log(input);
    }

    const Click = async(e) => {
        
        setLoading(true);
        let res = await fetch(baseUrl);
        if(res.status == 400)   // status code = 400 if the fetch operation fails.
            alert('Enter a valid URL');
        else{
             let data = await res.json();
             seturl(data);
             setList(...urlList,data);
             console.log(urlList);
            //  console.log(url);
            //  console.log(data);
            }
         setLoading(false);
            // console.log(res);
             // console.log(data);
            // // console.log(res);
            // setURL(res.data);
            // setList(urlList.push(res.data));
            // // console.log(baseUrl)
            // await axios.get(baseUrl).then((res) => {
            //     setURL(res.data);
            //     // console.log(response);
            //     setList(urlList.push(res.data));
            //     // console.log(response);
            //     // console.log(urlList);
             
            // console.log(error.response.data.error);
            // console.log('error')
              
            // console.log(error);
        }       
        
    
    return (
        <div className='inputBar'>

            <input 
            type="text"
            value={input}
            placeholder='Shorten the link here'
            onChange={Input}
             />

            <button onClick={Click}>Shorten</button>
        </div>
    )    
}

function InputContainer(){
    return(
        <InputBar/>
    )

}


export default InputContainer;
