// Component input Bar 

import React, { useState } from 'react';
import axios from "axios";
import './InputContainer.css'
// import './Components_styling/InputContainer.css'

function InputBar(){

    const [input,setInput] = useState("");
    const [url,setURL] = useState("");
    const [urlList,setList] = useState([]);
    const [loading,setLoading] = useState(false);

    const baseUrl = `https://api.shrtco.de/v2/shorten?url=${input}`

    const Input = (e) =>{
        setInput(e.target.value);
        // console.log(input);
    }

    const Click = async(e) => {
        
        setLoading(true);
        try{
            let res = await fetch(baseUrl);
            console.log(res);
            // console.log(res);
            // setURL(res.data);
            // setList(urlList.push(res.data));
            // // console.log(baseUrl)
            // await axios.get(baseUrl).then((res) => {
            //     setURL(res.data);
            //     // console.log(response);
            //     setList(urlList.push(res.data));
            //     // console.log(response);
            //     // console.log(urlList);
             }
             catch(error){
            // console.log(error.response.data.error);
            // console.log('error')
            alert('Enter a valid URL');   
            // console.log(error);
        }       
        setLoading(false);
    }
    return (
        <div className='inputBar'>

            <input 
            type="text"
            value={input}
            placeholder='Shorten the link here'
            onChange={Input}
             />

            <button onClick={Click}>Shorten</button>
        </div>
    )    
}

function InputContainer(){
    return(
        <InputBar/>
    )

}


export default InputContainer;
