// Component input Bar 

import React, { useState } from 'react';
import axios from "axios";
import './InputContainer.css'
// import './Components_styling/InputContainer.css'

function InputBar(){

    const [input,setInput] = useState("");
    const [url, setURL] = useState("");
    const [urlList,setList] = useState([]);
    const [loading,setLoading] = useState(false);

    let baseUrl = "https://api.shrtco.de/v2/shorten?url=${input}"

    const fetchURL = async(e) => {
        
        try{
            setLoading(true);
            let res = axios(baseUrl);
            console.log(res.data);
            setURL(res.data);
        }catch{

        }

        
    }
    return (
        <div className='inputBar'>

            <input 
            type="text"
            value={input}
            placeholder='Shorten the link here'
            onChange={ (e) => {setInput(e.target.value)}}
             />
            <button type='submit' onClick={ () => fetchURL} > Shorten </button>
        </div>
    )    
}

function InputContainer(){
    return(
        <InputBar/>
    )

}

export default InputContainer;
